property.value:
//LambdaExpression
(: number of statements should be limited, setters are ignored. Chained methods are one statement  :)
[(count(.//Statement) - count(.//StatementExpression/PrimaryExpression/PrimaryPrefix/Name[contains(@Image, '.set')]) > number($max))
(: nested Lambda :)
or .//LambdaExpression
]

rule.example:

class Foo {
       void bar(List additionals) {
            additionals.forEach(entity -> {
                if (entity.getKNr() != null) {
                    action.withActions(
                            new Action().withFoId(actionFoId.incrementAndGet())
                                    .withEntityPlanFoId(1)
                                    .withEntity(mapLogicType(true))
                    );
                }
            });
       }

       void baz(List additionals) {
            additionals.forEach(entity -> {         // bad, too many statements (5)
                if (entity.getKNr() != null) {
                    action.withActions(
                            new Action().withFoId(actionFoId.incrementAndGet())
                                    .withEntityPlanFoId(1)
                                    .withEntity(mapLogicType(true))
                    );
                }
                else { action.withActions(new Action().withFoId(0)); }
            });
        }

        public List<ProductEntity> booz(final Consent consent){
            List<ProductEntity> products = new ArrayList<>();
            consent.getScopes().forEach(
                scope -> {                                      // bad, too complex, nested lambda in lambda
                    List<ProductEntity> productEntities = scope.getProducts().stream().map(
                        product -> ProductEntity.builder()
                            .newEntity(true)
                            .state(State.VALID)
                            .build()
                        ).collect(toList());
                    products.addAll(productEntities);
                }
            );
            return products;
        }
}
