import java.util.*;
class MyClassOk {
    String header;
    String trailer;

    public boolean equals(Object o) {
        if (this == o) {
            return true;
        }

        if (o == null || getClass() != o.getClass()) {
            return false;
        }

        MyClassOk that = (MyClassOk) o;
        return header.equals(that.header) && trailer.equals(that.trailer);
    }
    public int hashCode() {
        return Objects.hash(header, trailer);
    }
}

