 import net.jcip.annotations.GuardedBy;
 public class Bad1 {
    @GuardedBy("this")
    private String txt;

    public String getText() {
        return txt; // bad
    }
    public void setText(String t) {
        txt = t; // bad
    }
}

public class Bad2 {
    @GuardedBy("this")
    private String txt;

    public String getText() {
        return txt; // bad
    }
    public synchronized void setText(String t) {
        txt = t;
    }
}

