public class Bad5 {
    @GuardedBy("this")
    private String txt;

    public String getText() {
        synchronized(this) {
            return txt;
        }
    }
    public void setText(String t) {
        txt = t; // bad
    }
}

public class Bad6 {
     @GuardedBy("this")
     private String txt;

     public String getText() {
         return txt; // bad
     }
     public void setText(String t) {
         synchronized(this) {
             txt = t;
         }
     }
 }

public class Bad7 {
    @GuardedBy("this")
    private String txt;
    private final Object LOCK = new Object();

    public String getText() {
        synchronized(LOCK) {
            return txt; // bad
        }
    }
    public String setText(String t) {
        synchronized(LOCK) {
            txt = t; // bad
        }
    }
}

class Bad8 {
    @GuardedBy("this")
    private String txt;
    private final AtomicBoolean ab = new AtomicBoolean();

    public void modifyText() {
        txt += "-appended"; //bad
    }

    public void consumeText() {
        ab.set(txt.equals("bla")); //bad
    }
}

public class Good1 {
    @GuardedBy("this")
    private String txt;

    public synchronized String getText() {
        return txt;
    }
    public synchronized void setText(String t) {
        txt = t;
    }
}

public class Good2 {
    @GuardedBy("this")
    private String txt;

    public String getText() {
        synchronized(this) {
            return txt;
        }
    }
    public void setText(String t) {
        synchronized(this) {
            txt = t;
        }
    }
}

class Good3 {
     @GuardedBy("this")
     private String txt;
     private final AtomicBoolean ab = new AtomicBoolean();

     public void modifyText() {
         synchronized(this) {
             txt += "-appended";
         }
     }

     public void consumeText() {
         synchronized(this) {
             ab.set(txt.equals("bla"));
         }
     }

     @Autowired
     public void setText(String t) {
         txt = t;
     }
 }
